body {
  background-color: #F5F0F2;
  font-family: "Trebuchet MS", Helvetica, sans-serif;
  color: #333; }

h1 {
  background-color: #F5F0F2;
  font-family: 'Bungee Shade', cursive;
  font-size: 5em;
  margin-top: 50px;
  color: #e12D53;
  display: block;
  text-align: center; }

#favourites {
  position: absolute;
  top: 30px;
  right: 30px; }

#favourites a:hover {
  color: #e12D53; }

#search-form {
  width: 50%;
  margin-left: auto;
  margin-right: auto; }

.search-group {
  display: grid;
  grid-template-columns: 1fr 80% 1fr;
  justify-items: stretch;
  align-items: center;
  border: 3px solid #333;
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
  transition: 0.3s;
  background: #fff;
  border-radius: 10px; }

.card-container {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-gap: 20px 20px;
  justify-items: stretch;
  align-items: stretch;
  max-width: 69em;
  list-style: none;
  margin: 30px auto;
  padding: 0; }

.card {
  /* Add shadows to create the "card" effect */
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
  transition: 0.3s;
  width: 200px; }

.card img {
  outline: none;
  border: none;
  display: block;
  width: 200px; }

/* On mouse-over, add a deeper shadow */
.card:hover {
  box-shadow: 0 8px 16px 0 rgba(0, 0, 0, 0.2); }

/* Add some padding inside the card container */
.card-body {
  padding: 2px 16px;
  display: grid;
  grid-template-columns: 90% 1fr;
  grid-gap-columns: 10px;
  align-items: center; }

.action {
  height: 40px;
  background: transparent;
  border-color: transparent;
  border-style: none;
  cursor: pointer; }

#delete-btn {
  visibility: hidden; }

input {
  border: none;
  flex-basis: 80%;
  font-size: 1.2em; }

input:focus {
  border: none;
  box-shadow: none !important;
  outline: none; }
